$vmsCameraParams = @{ Camera = Get-VmsCamera -Name 'Office Entrance' Coordinates = '45.4171601197572, -122.732137977298' Direction = 90 FieldOfView = 180 Depth = 15 Verbose = $true }
Set-VmsCamera @vmsCameraParams
<# OUTPUT VERBOSE: Performing the operation "Changing CoverageDirection from 0.505555555555556 to 0.25" on target "Cam Lab Camera". VERBOSE: Performing the operation "Changing CoverageFieldOfView from 0.125 to 0.5" on target "Cam Lab Camera". VERBOSE: Performing the operation "Changing CoverageDepth from 37.49040000015 to 4.57200000001829" on target "Cam Lab Camera". VERBOSE: Performing the operation "Saving changes" on target "Cam Lab Camera". #>

foreach ($camera in Select-Camera -AllowFolders -RemoveDuplicates) {
    $streams = $camera | Get-VmsCameraStream

    $streams | Where-Object LiveMode -eq 'Never' | Set-VmsCameraStream -LiveMode WhenNeeded
    $streams[0] | Set-VmsCameraStream -LiveDefault -Recorded

    if ($streams.Count -gt 1 ) {
        $streams[1..($streams.Count - 1)] | Set-VmsCameraStream -Disabled
    }
}
